import React from 'react'
import {
    Label,
    Input,
    Textarea,
} from '@rebass/forms'
import {
    Box, Flex, Button, Heading
} from 'rebass';

class Contact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: ''
        }
    }

    render() {
        return (
            <Box
                as='form'
                onSubmit={e => e.preventDefault()}
                py={3}>
                <Heading>Contact Me</Heading>
                <hr></hr>
                <Flex mx={-2} mb={3}>
                    <Box width={3 / 4} px={2}>
                        <Label htmlFor='name'>Name</Label>
                        <Input
                            id='name'
                            name='name'
                            onChange={handleChange}
                            value={''}
                        />
                    </Box>
                </Flex>

                <Box mb={3} width={3 / 4}>
                    <Label htmlFor='email'>Email</Label>
                    <Input
                        id='email'
                        name='email'
                        type='email'
                        placeholder='jane@example.com'
                        onChange={handleChange}
                        value={''}
                    />
                </Box>

                <Flex mx={-2} mb={3}>
                    <Box width={3 / 4} px={2}>
                        <Label htmlFor='location'>Comments</Label>
                        <Textarea
                            id='message'
                            name='message'
                            type='textarea'
                            placeholder='comments'
                            onChange={handleChange}
                            value={''}>

                        </Textarea>
                    </Box>
                </Flex>
                <Flex mx={-2} flexWrap='wrap'>
                    <Box px={2}>
                        <Button variant='primary' mr={2}
                            formValues={this.state}
                            email='cfor0@outlook.com'>
                            Submit
                        </Button>
                    </Box>
                </Flex>
            </Box>
        )
    }
}

export default Contact
