import React from 'react';
import {
    Box,
    Card,
    Image,
    Heading,
    Text,
    Flex,
    Link
} from 'rebass';
import linkPic from '../assets/images/linkPic.jpg'

const aboutStyle = {
    fontSize: 16,
    marginLeft: 5,
    marginRight: 5,
    paddingLeft: 2
}
function About() {
    return (
        <Flex mx={2} my={2}>
            <Box
                sx={{
                    px: 4,
                    py: 6,
                    backgroundImage: 'url(https://source.unsplash.com/random/1024x768?sky)',
                    backgroundSize: 'cover',
                    borderRadius: 8,
                    color: 'white',
                    bg: 'gray',
                }}>
                <Heading
                    textAlign='center'
                    fontSize={[5, 6]}>
                    Background Image
  </Heading>
            </Box>
           
        </Flex>
    )
}

export default About
