import React from 'react'
import {
    Label,
    Input,
    Select,
    Textarea,
    Radio,
    Checkbox,
} from '@rebass/forms'
import {
    Box, Flex, Button
} from 'rebass';

class Contact extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            email: '',
            message: ''
        }
    }

    render() {
        return (
            <Box
                as='form'
                onSubmit={e => e.preventDefault()}
                py={3}>
                <Flex mx={-2} mb={3}>
                    <Box width={1 / 2} px={2}>
                        <Label htmlFor='name'>Name</Label>
                        <Input
                            id='name'
                            name='name'
                            placeHolder='Jane Doe'
                        />
                    </Box>
                    <Box width={1 / 2} px={2}>
                        <Label htmlFor='location'>Comments</Label>
                        <Textarea placeholder='comments'></Textarea>
                    </Box>
                </Flex>
                <Flex mx={-2} flexWrap='wrap'>
                    <Box px={2} ml='auto'>
                        <Button>
                            Beep
                        </Button>
                    </Box>
                </Flex>
            </Box>
        )
    }
}

export default Contact
