import React from 'react';
import {
    Box,
    Card,
    Image,
    Heading,
    Text,
    Flex,
    Link
} from 'rebass';

const aboutStyle = {
    fontSize: 16,
    marginLeft: 5,
    marginRight: 5
}
function About() {
    return (
        <Flex mx={2} my={2}>
            <Box width={1/2}>
                <Image lx={{
                    // width: [200],
                    // height: [250],
                    borderRadius: 8,
                }}  src='https://source.unsplash.com/random/512x384?space' />
            </Box>
            <Box width={1/2}>
            <Text style={aboutStyle}>I am a Full-Stack Developer.<br></br>
                    After graduating with a major in music, my career pursuit led me on a journey towards a Full-Stack Development Bootcamp where I found a passion for Web-Development. Like music, I have learned to re-wire my brain to this unique way of thinking and problem-solving. I am excited to increase my knowledge in this fast-growing field of Web-Development.</Text>
            </Box>
        </Flex>
    )
}

export default About
