import React from 'react'
import { ThemeProvider } from 'emotion-theming';
import theme from '@rebass/preset';
import NavBar from './NavBar'
import '../App.css';
import {
    Box, Link
} from 'rebass';
import ProjectInfo from './ProjectInfo'
import Macklist from '../assets/images/Macklist.gif'
import Truckin from '../assets/images/Truckin.gif'


class Projects extends React.Component {
    render() {
        return (
            <ThemeProvider theme={theme}>
                <NavBar />
                <Box
                    sx={{
                        px: 4,
                        py: 4,
                        height: ['100%'],
                        backgroundImage: 'url(https://images.unsplash.com/photo-1477346611705-65d1883cee1e?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1024&q=80)',
                        backgroundSize: 'cover',
                        // borderRadius: 8,
                        color: 'white',
                        bg: 'gray',
                    }}>
                    <ProjectInfo image={Macklist} text='MackList is a data management website inspired by "Craigslist", where users can login, view, and create posts. Users can filter posts in cities throughout United States and contact seller if interested!'>
                        <Link href='http://macklist.herokuapp.com/'>Macklist </Link>
                    </ProjectInfo>

                    <ProjectInfo text="Truckin' is a web application designed for an administrator to create customers and assign them to any driver on the website. This app is meant for small buisnesses that deliver their product. The administrator can create and delete customers if needed using Sequelize.">
                        <Link href='https://calm-cliffs-47930.herokuapp.com/'>Truckin'</Link>
                    </ProjectInfo>
                    <ProjectInfo>Peruse For Brews</ProjectInfo>
                    <ProjectInfo>Clicky Game</ProjectInfo>
                    <ProjectInfo>Customer View</ProjectInfo>
                    <Box height='300px'></Box>
                </Box>
            </ThemeProvider>

        )
    }
}

export default Projects
